package loopdemo;


public class Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			
		int a , b, row = 6;
		
		for (a = 1; a < row;  a++) {
			
			for (b=1; b <= a; b++) {
				
				System.out.print("* ");
				}
			
			System.out.println();
			
		}
		
	}	
}
